tinymce.PluginManager.add('sf_image', function(editor) {
	editor.addButton('sf_image', {
		icon: 'image',
		tooltip: 'Insert/edit image',
		onclick: function() {
			editor.windowManager.open({
				url: 'image.htm',
				width: 520,
				height: 380
			});
		},
		stateSelector: 'img:not([data-mce-object])'
	});
});